import {
  Backdrop,
  Container,
  Fade,
  makeStyles,
  Modal,
} from "@material-ui/core";
import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  addClientManagement,
  hidenFormClient,
} from "../../store/actions/clientManagement.action";
import "./scss/FromAddClient.css";

const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
}));

function FromAddClient() {
  const classes = useStyles();
  const dispatch = useDispatch();

  // modal
  const addSuccess = useSelector((state) => {
    return state.ClientManagementReducer?.addSuccess || {};
  });

  const [open, setOpen] = React.useState(false);

  const handleClose = () => {
    setOpen(false);
  };
  // modal

  useEffect(() => {
    validButtonSubmit();
  });

  useEffect(() => {
    if (addSuccess && addSuccess.status === 200) {
      setOpen(true);
    }
  }, [addSuccess, addSuccess.status]);

  const [addClient, setAddClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau: "",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "",
    hoTen: "",
  });

  const [validClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau: "",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "",
    hoTen: "",
  });
  const [validSubmit, setValidSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value, pattern, type } = e.target;
    setAddClient({ ...addClient, [name]: value });

    // check pattern
    let regex = new RegExp(pattern);
    // valid check empty
    if (value.trim() === "") {
      validClient[name] = "Do Not Empty";
    } else {
      validClient[name] = "";
    }
    // valid check empty
    if (name === "maNhom") {
      if (!value) {
        validClient[name] = "Chọn Mã Nhóm";
      }
    }
    if (name === "maLoaiNguoiDung") {
      if (!value) {
        validClient[name] = "Chọn Mã Người Dùng";
      }
    }
    // valid check empty

    // check password
    if (name === "confirmMatKhau") {
      if (value === addClient["matKhau"]) {
        validClient[name] = "";
      } else {
        validClient[name] = "Mật Khẩu Không Trùng Nhau !";
      }
    }
    // check password

    // check email
    if (type === "email") {
      if (!value) {
        validClient[name] = "Do Not Empty !";
      } else if (!regex.test(value)) {
        validClient[name] = "Email Invaild !";
      }
    }
    // check email
  };
  const validButtonSubmit = () => {
    let valid = true;
    for (let key in validClient) {
      if (validClient[key] !== "" || addClient[key] === "") {
        valid = false;
      }
    }
    setValidSubmit(valid);
  };
  //   submit
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addClientManagement(addClient));
    // dispatch(hidenFormClient("listUser"));
  };
  const hidenFormEdit = () => {
    dispatch(hidenFormClient("listUser"));
  };

  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  return (
    <div className="backgroundFormAdd">
      <div className="wrapFromAdd">
        <Container maxWidth="md">
          <h4>Thêm Người Dùng</h4>
          <form onSubmit={handleSubmit}>
            <input
              onChange={handleChange}
              value={addClient.taiKhoan}
              placeholder="Tài Khoản"
              name="taiKhoan"
              type="text"
            />
            <span>{validClient.taiKhoan}</span>

            <input
              onChange={handleChange}
              value={addClient.matKhau}
              placeholder="Mật Khẩu"
              name="matKhau"
              type="password"
            />
            <span>{validClient.matKhau}</span>

            <input
              onChange={handleChange}
              value={addClient.confirmMatKhau}
              placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
              name="confirmMatKhau"
              type="password"
            />
            <span>{validClient.confirmMatKhau}</span>

            <input
              onChange={handleChange}
              value={addClient.hoTen}
              placeholder="Họ Tên"
              name="hoTen"
              type="text"
            />
            <span>{validClient.hoTen}</span>

            <input
              onChange={handleChange}
              value={addClient.email}
              placeholder="Email"
              name="email"
              type="email"
              pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
            />
            <span>{validClient.email}</span>

            <input
              onChange={handleChange}
              value={addClient.soDt}
              placeholder="Số Điện Thoại"
              name="soDt"
            />
            <span>{validClient.soDt}</span>
            <br />

            <select
              name="maLoaiNguoiDung"
              value={addClient.maLoaiNguoiDung}
              onChange={handleChange}
            >
              <option value="">Loại Người Dùng</option>
              <option value="QuanTri">Quản Trị</option>
              <option value="KhachHang">Khách Hàng</option>
            </select>

            <select
              name="maNhom"
              value={addClient.maNhom}
              onChange={handleChange}
            >
              <option value="">Mã Nhóm</option>
              {renderMaNhom()}
            </select>
            <br />
            <span style={{ marginRight: "20px" }}>
              {validClient.maLoaiNguoiDung}
            </span>
            <span>{validClient.maNhom}</span>

            <div style={{ textAlign: "center" }}>
              <button
                type="button"
                style={{ cursor: "pointer", color: "red" }}
                onClick={hidenFormEdit}
              >
                Cancel
              </button>
              {validSubmit ? (
                <button type="submit" style={{ cursor: "pointer" }}>
                  Add
                </button>
              ) : (
                <button disabled style={{ cursor: "no-drop" }}>
                  Add
                </button>
              )}
            </div>
          </form>
        </Container>
      </div>

      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className="modalAddClient">
            <p>{addSuccess?.data.taiKhoan}</p>
            <p>{addSuccess?.data.matKhau}</p>
            <p>{addSuccess?.data.hoTen}</p>
            <p>{addSuccess?.data.email}</p>
            <p>{addSuccess?.data.soDt}</p>
            <p>{addSuccess?.data.maNhom}</p>
            <p>{addSuccess?.data.maLoaiNguoiDung}</p>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}

export default FromAddClient;
