import React, { useEffect } from "react";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { Grid } from "@material-ui/core";
import { useSelector } from "react-redux";
import "./scss/CinemaDetailPage.css";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { getDetailCinema } from "../../store/actions/cinemaDetail.action";
import { useState } from "react";

export default function CinemaDetailPage() {
  const dispatch = useDispatch();
  const hisTory = useHistory();

  const [stateMaCumRap, setStateMaCumRap] = useState({
    maHeThongRap: "",
  });

  const listShowTime = useSelector((state) => {
    return state.TabNavigationPageReducer.listShowTime; // get data to TabNavigationPageReducer
  });

  const handleLogo = (maHeThongRap) => {
    hisTory.push(`/cinemaDetail/${maHeThongRap}`); // post maCumRap => CinemaDetailComponent
  };

  useEffect(() => {
    dispatch(getDetailCinema(stateMaCumRap.maHeThongRap));
  });

  const renderLoGo = () => {
    return listShowTime.map((item, index) => {
      return (
        <Grid item lg={2} key={index}>
          <button
            className="btnimgCinemaS"
            onClick={() => {
              handleLogo(
                setStateMaCumRap({
                  maHeThongRap: item.maHeThongRap,
                })
              );
            }}
          >
            <img src={item.logo} alt="" className="imgCinemaS" />
          </button>
        </Grid>
      );
    });
  };

  return (
    <Accordion>
      <AccordionSummary expandIcon={<ExpandMoreIcon />}>
        <Typography className="titleSimple">Chọn Rạp</Typography>
      </AccordionSummary>
      <AccordionDetails>
        <Grid container>{renderLoGo()}</Grid>
      </AccordionDetails>
    </Accordion>
  );
}
