import { Button, Container, Grid } from "@material-ui/core";
import React, { useState } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
// date format
import format from "date-format";

const CheckInfoMaTion = () => {
  const listPhimBooking = useSelector((state) => {
    return state.BookingCodePhimReducer.listPhimBooking;
  });

  const infoListChair = useSelector((state) => {
    return state.BookingReducer.listChair; // get data BookingReducer
  });

  const [stateListChairChoi] = useState({
    listChair: [],
  });
  const hanldeCheckout = () => {
    stateListChairChoi.listChair = infoListChair.filter(
      (chair) => chair.dangChon
    );
  };
  console.log(stateListChairChoi.listChair);
  // recive to componet/Showtimecomponent
  const { showTimeCode } = useParams();
  let arrShowTimeCode = showTimeCode.split("-");
  let maCumRap = arrShowTimeCode[2];
  let tenCumRap = arrShowTimeCode[3];

  return (
    <Container>
      <Grid item xs={12} md={12} lg={12} container>
        <Grid item xs={12} md={5} lg={4}>
          <img src={listPhimBooking.hinhAnh} alt="" className="movie_bg" />
        </Grid>
        <Grid item xs={12} md={7} lg={4} className="movie_intro">
          <span>
            <p>
              Tên Cụm Rạp : {maCumRap}-{tenCumRap}
            </p>
            <p>Tên Phim : {listPhimBooking.tenPhim}</p>
            <p>
              Ngày Chiếu :{" "}
              {format("dd-mm-yyyy", new Date(listPhimBooking.ngayKhoiChieu))}
            </p>
            <p>
              Giờ Chiếu :{" "}
              {format("hh:mm", new Date(listPhimBooking.ngayKhoiChieu))}
            </p>
          </span>
        </Grid>
        <Grid item xs={12} md={7} lg={4} className="movie_intro"></Grid>
      </Grid>
      <Grid item lg={12} style={{ textAlign: "center" }}>
        <Button onClick={hanldeCheckout}>Booking</Button>
      </Grid>
    </Container>
  );
};

export default CheckInfoMaTion;
