import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import MovieIcon from "@material-ui/icons/Movie";
import PersonOutlineRoundedIcon from "@material-ui/icons/PersonOutlineRounded";
import SupervisorAccountRoundedIcon from "@material-ui/icons/SupervisorAccountRounded";
import MovieManagement from "../../components/MovieManagement/MovieManagement";
import ClientMaragement from "../../components/ClientManagement/ClientMaragement";
import "./scss/admin.css";
import { Button, Grid } from "@material-ui/core";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import { useDispatch, useSelector } from "react-redux";
import { getMovieList_Action } from "../../store/actions/movie.action";

const useStyles = makeStyles((theme) => ({
  content: {
    width: "100%",
    padding: theme.spacing(3),
  },
  paper: {
    padding: theme.spacing(2),
    color: "gray",
    backgroundColor: "rgba(255, 255, 255, 0.25)",
    boxShadow: "rgba(142, 142, 142, 0.19) 0px 6px 15px 0px",
    borderRadius: "5px",
  },
}));

export default function Admin() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [maNhom, setStateMaNhom] = useState("");
  // tab panel
  const [open, setOpen] = React.useState("admin");
  const showPageAdmin = () => {
    if (open === "client") {
      return <ClientMaragement />;
    } else if (open === "movie") {
      return <MovieManagement />;
    }
  };
  // tab panel

  const movieList = useSelector((state) => {
    return state.movieReducer.movieList;
  });

  useEffect(() => {
    dispatch(getMovieList_Action("GP01"));
  });

  const getMaNhom = (e) => {
    const { value } = e.target;
    dispatch(getMovieList_Action(value));
  };
  console.log(movieList);
  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };
  const renderAdminPage = () => {
    return (
      <Container>
        <select name="maNhom" value={maNhom} onChange={getMaNhom}>
          {renderMaNhom()}
        </select>
        <Grid container spacing={2}>
          <Grid item lg={6}>
            <Paper className={classes.paper}>xs</Paper>
          </Grid>
          <Grid item lg={6}>
            <Paper className={classes.paper}>xs</Paper>
          </Grid>
        </Grid>
      </Container>
    );
  };

  // render
  return (
    <>
      <Button
        onClick={() => {
          setOpen("admin");
        }}
      >
        <SupervisorAccountRoundedIcon />
      </Button>
      <Button
        onClick={() => {
          setOpen("movie");
        }}
        label="Item One"
      >
        <MovieIcon label="Item One" />
      </Button>
      <Button
        onClick={() => {
          setOpen("client");
        }}
      >
        <PersonOutlineRoundedIcon />
      </Button>

      <main className={classes.content}>
        {open === "admin" ? renderAdminPage() : showPageAdmin()}
      </main>
    </>
  );
}
