import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getInfoUserAction } from "../../store/actions/getInfoUser.action";
import "./css/profile.css";
import Loader from "../../components/Loader/Loader";
import format from "date-format";
import { Grid } from "@material-ui/core";

export default function ProfileUser() {
  const dispatch = useDispatch();
  const taiKhoan = JSON.parse(localStorage.getItem("taiKhoan"));

  useEffect(() => {
    dispatch(getInfoUserAction(taiKhoan));
  }, [dispatch, taiKhoan]);

  let loading = useSelector((state) => state.CommonReducer.loading);

  const maLoaiNguoiDung = JSON.parse(localStorage.getItem("maLoaiNguoiDung"));
  let infoUser = useSelector((state) => state.infoUserReducer);

  const [stateSearch, setStateSearch] = useState("");
  // const searchTenPhim = (event) => {
  //   const { name, value } = event.target;
  //   setStateSearch({ ...stateSearch, [name]: value });
  //   infoUser.thongTinDatVe = infoUser.thongTinDatVe.filter((item) =>
  //     item.tenPhim
  //       .toLocaleLowerCase()
  //       .match(stateSearch.search.toLocaleLowerCase())
  //   );
  // };
  console.log(infoUser.thongTinDatVe);
  const renderLichSuDatVe = () => {
    return infoUser.thongTinDatVe
      ?.filter((item) => {
        if (stateSearch === "") {
          return item;
        } else if (
          item.tenPhim
            .toLocaleLowerCase()
            .includes(stateSearch.toLocaleLowerCase())
        ) {
          return item;
        }
      })
      .map((donHang, index) => {
        let soLuong = donHang.danhSachGhe.length;
        return (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{donHang.tenPhim}</td>
            <td>{donHang.danhSachGhe[0].tenHeThongRap}</td>
            <td>{format("dd-mm-yyyy", new Date(donHang.ngayDat))}</td>
            <td>{format("hh:mm", new Date(donHang.ngayDat))}</td>
            <td>{soLuong}</td>

            <td>{donHang.giaVe * soLuong}</td>
          </tr>
        );
      });
  };

  return loading ? (
    <Loader />
  ) : (
    <div className="profileUser">
      <Grid container>
        <Grid item lg={4} className="infoUser">
          <h5>Thông Tin Của Bạn</h5>
          <table className="table">
            <thead>
              <tr>
                <td>Họ tên: </td>
                <td>{infoUser.hoTen}</td>
              </tr>
              <tr>
                <td>Email: </td>
                <td>{infoUser.email}</td>
              </tr>
              <tr>
                <td>Số điện thoại: </td>
                <td>{infoUser.soDT}</td>
              </tr>
              <tr>
                <td>Tài khoản: </td>
                <td>{infoUser.taiKhoan}</td>
              </tr>
              <tr>
                <td>Mật khẩu: </td>
                <td>{infoUser.matKhau}</td>
              </tr>
              <tr>
                <td>Vai trò: </td>
                <td>
                  {maLoaiNguoiDung === "QuanTri"
                    ? "Quản trị viên"
                    : "Khách hàng"}
                </td>
              </tr>
            </thead>
          </table>
          <div style={{ textAlign: "center", marginTop: "20px" }}>
            <button>Cập nhật thông tin</button>
          </div>
        </Grid>
        <Grid item lg={7} className="historyBooking">
          <h5>Lịch Sử Đặt Vé</h5>
          <input
            type="text"
            placeholder="Search"
            className="inputSearch"
            onChange={(e) => {
              setStateSearch(e.target.value);
            }}
          />
          <table className="tableBooking">
            <thead>
              <tr>
                <th>STT</th>
                <th>Tên Phim</th>
                <th>Cụm rạp</th>
                <th>Ngày đặt</th>
                <th>Xuất Chiếu</th>
                <th>Số lượng</th>
                <th>Thành tiền</th>
              </tr>
            </thead>
            <tbody>{renderLichSuDatVe()}</tbody>
          </table>
        </Grid>
      </Grid>
    </div>
  );
}
