import * as React from "react";
import PropTypes from "prop-types";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Box from "@material-ui/core/Box";
import { Container, Grid } from "@material-ui/core";
// date format
import format from "date-format";
import { useDispatch, useSelector } from "react-redux";
import { getListShowTimePageAction } from "../../store/actions/tabNavigationPage.action";

// function tabpanel
function TabPanel(props) {
  const { children, value, index, ...other } = props;
  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 2 }}>
          <div>{children}</div>
        </Box>
      )}
    </div>
  );
}
TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};
function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}
// function tabpanel

const DetailTabComponent = (props) => {
  // tabpanel
  const [value, setValue] = React.useState(0);
  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  // tabpanel

  const listShowTime = useSelector((state) => {
    return state.TabNavigationPageReducer.listShowTime; // get data to TabNavigationPageReducer
  });
  console.log(listShowTime);
  const dispatch = useDispatch();

  React.useEffect(() => {
    dispatch(getListShowTimePageAction()); // get data to Axios tabNavigationPage.action
  }, [dispatch]);

  const handleCodeCinema = (codeCinema) => {
    console.log(codeCinema);
  };

  const renderLoGo = () => {
    return listShowTime.map((item, index) => {
      return (
        <Grid item xs={12} key={index}>
          <button
            onClick={() => {
              handleCodeCinema(item.maHeThongRap);
            }}
          >
            <img src={item.logo} alt="" className="images" />
            {item.tenHeThongRap}
          </button>
        </Grid>
      );
    });
  };

  const { ngayKhoiChieu, maPhim, moTa, tenPhim, biDanh, maNhom } = props.detail;

  return (
    <div className="detailTab">
      <Container>
        <Box sx={{ width: "100%" }}>
          <Box sx={{ borderBottom: 1, borderColor: "divider" }}>
            <Tabs value={value} onChange={handleChange} className="tabs">
              <Tab label="Thông Tin" {...a11yProps(0)} />
              <Tab label="Lịch Chiếu" {...a11yProps(1)} />
            </Tabs>
          </Box>
          <TabPanel value={value} index={0}>
            <Grid container>
              <Grid lg={2} item>
                <ul>
                  <li>Ngày Khởi Chiếu : </li>
                  <li>Mã Phim :</li>
                  <li>Tên Phim :</li>
                  <li>Bí Danh :</li>
                  <li>Mã Nhóm :</li>
                </ul>
              </Grid>
              <Grid lg={2} item>
                <ul>
                  <li>
                    {format("mm-dd-yyyy , hh-mm", new Date(ngayKhoiChieu))}
                  </li>
                  <li>{maPhim}</li>
                  <li>{tenPhim}</li>
                  <li>{biDanh}</li>
                  <li>{maNhom}</li>
                </ul>
              </Grid>
              <Grid lg={8} item>
                Mô Tả : {moTa}
              </Grid>
            </Grid>
          </TabPanel>
          <TabPanel value={value} index={1}>
            <Grid container className="navigaToDetail">
              <Grid container item xs={4} className="rowOne">
                {renderLoGo()}
              </Grid>
              <Grid container item xs={8} className="rowTwo">
                2
              </Grid>
            </Grid>
          </TabPanel>
        </Box>
      </Container>
    </div>
  );
};
export default DetailTabComponent;
