import Axios from "axios";
import { DOMAIN } from "../../services/domainUrl";
import { DELETE_LIST_CLIENT_MANAGEMENT, GET_LIST_CLIENT_MANAGEMENT, GET_LIST_SEARCH_CLIENT_MANAGEMENT, MESAGE_DELETE_CLIENT_MANAGEMENT, TAIKHOAN_CLIENT_MANAGEMENT } from "../constants/clientManagement.constant";

export const getListClientManagement = (maNhom) => {
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "GET",
                url: `${DOMAIN}QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=GP01`
            })
            dispatch({
                type: GET_LIST_CLIENT_MANAGEMENT,
                payload: res.data
            })
        } catch (error) {
            console.log(error);
        }
    }
}

export const getListSearchClientManagement = (tuKhoa) => {
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "GET",
                url: `${DOMAIN}QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=GP01&tuKhoa=${tuKhoa}`
            })
            dispatch({
                type: GET_LIST_SEARCH_CLIENT_MANAGEMENT,
                payload: res.data
            })
        } catch (error) {
            console.log(error);
        }
    }
}

export const deleteListClientManagement = (taiKhoan) => {
    console.log(taiKhoan);
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "DELETE",
                url: `${DOMAIN}QuanLyNguoiDung/XoaNguoiDung?TaiKhoan=${taiKhoan}`
            })
            dispatch({
                type: TAIKHOAN_CLIENT_MANAGEMENT,
                payload: taiKhoan,
            })
            dispatch({
                type: DELETE_LIST_CLIENT_MANAGEMENT,
                payload: res.data
            })
            console.log(res.data);
        } catch (error) {
            console.log(error.response.data);
            dispatch({
                type: MESAGE_DELETE_CLIENT_MANAGEMENT,
                payload: error.response.data
            })
        }
    }
}
