import { GET_LOGO_DETAILTAB, GET_MOVIE_DETAIL } from "../constants/detailTabComponent.const"

const initialState = {
    listLogoDetail: [],
    detailMovie: {},
    lichChieuMovie: [],
}

export const DetailTabReducer = (state = initialState, action) => {
    const { type, payload } = action
    switch (type) {
        case GET_LOGO_DETAILTAB: {
            state.listShowTimeDetail = payload
            return { ...state }
        }
        case GET_MOVIE_DETAIL: {
            console.log(payload);
            state.detailMovie = payload;
            state.lichChieuMovie = payload.lichChieu;
            return { ...state };
        }
        default:
            return state
    }
}
