import { Container } from "@material-ui/core";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  addClientManagement,
  hidenFormClient,
} from "../../store/actions/clientManagement.action";
import "./scss/FromAddClient.css";

function FromAddClient() {
  const dispatch = useDispatch();

  const [addClient, setAddClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau:"",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "",
    hoTen: "",
  });

  const [validClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau:"",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "",
    hoTen: "",
  });
  const [validSubmit, setValidSubmit] = useState(true);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAddClient({ ...addClient, [name]: value });

    // valid
    if (value.trim() === "") {
      validClient[name] = "Do Not Empty";
    } else {
      validClient[name] = "";
    }
    if (validClient[name] !== "") {
      setValidSubmit(false);
    } else {
      setValidSubmit(true);
    }
    // valid
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addClientManagement(addClient));
    dispatch(hidenFormClient("listUser"));
  };
  const hidenFormEdit = () => {
    dispatch(hidenFormClient("listUser"));
  };

  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  return (
    <div className="backgroundFormAdd">
      <div className="wrapFromAdd">
        <Container maxWidth="md">
          <h4>Thêm Người Dùng</h4>
          <form onSubmit={handleSubmit}>
            <input
              onChange={handleChange}
              value={addClient.taiKhoan}
              placeholder="Tài Khoản"
              name="taiKhoan"
              type="text"
            />
            <span>{validClient.taiKhoan}</span>

            <input
              onChange={handleChange}
              value={addClient.matKhau}
              placeholder="Mật Khẩu"
              name="matKhau"
              type="password"
            />
            <span>{validClient.matKhau}</span>

            <input
              onChange={handleChange}
              value={addClient.confirmMatKhau}
              placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
              name="confirmMatKhau"
              type="password"
            />
            <span>{validClient.confirmMatKhau}</span>

            <input
              onChange={handleChange}
              value={addClient.hoTen}
              placeholder="Họ Tên"
              name="hoTen"
              type="text"
            />
            <span>{validClient.hoTen}</span>

            <input
              onChange={handleChange}
              value={addClient.email}
              placeholder="Email"
              name="email"
              type="email"
              pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
            />
            <span>{validClient.email}</span>

            <input
              onChange={handleChange}
              value={addClient.soDt}
              placeholder="Số Điện Thoại"
              name="soDt"
            />
            <span>{validClient.soDt}</span>
            <br />

            <select
              name="maLoaiNguoiDung"
              value={addClient.maLoaiNguoiDung}
              onChange={handleChange}
            >
              <option>Loại Người Dùng</option>
              <option value="QuanTri">Quản Trị</option>
              <option value="KhachHang">Khách Hàng</option>
            </select>

            <select
              name="maNhom"
              value={addClient.maNhom}
              onChange={handleChange}
            >
              <option>Mã Nhóm</option>
              {renderMaNhom()}
            </select>
            <br />
            <span>{validClient.maNhom}</span>
            <span>{validClient.maLoaiNguoiDung}</span>

            <div style={{ textAlign: "center" }}>
              <button
                type="button"
                style={{ cursor: "pointer", color: "red" }}
                onClick={hidenFormEdit}
              >
                Cancel
              </button>
              {validSubmit ? (
                <button type="submit" style={{ cursor: "pointer" }}>
                  Update
                </button>
              ) : (
                <button disabled style={{ cursor: "no-drop" }}>
                  Update
                </button>
              )}
            </div>
          </form>
        </Container>
      </div>
    </div>
  );
}

export default FromAddClient;
