import { GET_CINEMA_DROPDOWNS, GET_LIST_MOVIE_DROPDOWNS } from "../constants/dropdown.constant"

const initialState = {
    listPhim: [],
    listCinema: {},
}

export const dropDownsReducer = (state = initialState, { type, payload }) => {
    switch (type) {
        case GET_LIST_MOVIE_DROPDOWNS:
            state.listPhim = payload
            return { ...state, ...payload }

        case GET_CINEMA_DROPDOWNS:
            state.listCinema = payload.heThongRapChieu
            return { ...state, ...payload }
        default:
            return state
    }
}
