/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect } from "react";
import "./scss/dropdowns.css";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { useDispatch, useSelector } from "react-redux";
import {
  getListMovieDropDowns,
  getCinemaDropDownsWithCode,
} from "../../store/actions/dropdowns.action";

const DropDowns = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getListMovieDropDowns());
  }, [dispatch]);

  const listMovie = useSelector((state) => {
    return state.dropDownsReducer.listPhim;
  });

  const listCinema = useSelector((state) => {
    return state.dropDownsReducer.listCinema;
  });

  const getCinemaDropDowns_ = (maPhim) => {
    dispatch(getCinemaDropDownsWithCode(maPhim));
  };

  const renderListMovie = () => {
    return listMovie.map((item, index) => {
      return (
        <a key={index} onClick={() => getCinemaDropDowns_(item.maPhim)}>
          {item.tenPhim}
        </a>
      );
    });
  };
  console.log(listCinema.heThongRapChieu);
  const renderListCinema = () => {
    return listCinema.heThongRapChieu.map((item, index) => {
      console.log(item);
      return <a key={index}>{item.tenHeThongRap}</a>;
    });
  };
  return (
    <div className="container dropDownsRelavite">
      <div className="dropDownsMain">
        <div className="dropdownPhim">
          <button className="dropbtn">Phim</button>
          <ExpandMoreIcon />
          <div className="contentPhim">{renderListMovie()}</div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Rạp</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            {!listCinema && !listCinema.heThongRapChieu ? (
              renderListCinema()
            ) : (
              <a href="#">Link 1</a>
            )}
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Ngày Xem</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Xuất Chiếu</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
        </div>

        <button className="btnMuaVe">Mua Vé Ngay</button>
      </div>
    </div>
  );
};
export default DropDowns;
