/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from "react";
import "./scss/dropdowns.css";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { useDispatch, useSelector } from "react-redux";
import {
  getListMovieDropDowns,
  getCinemaDropDownsWithCode,
} from "../../store/actions/dropdowns.action";

const DropDowns = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getListMovieDropDowns());
  }, [dispatch]);

  const listMovie = useSelector((state) => {
    return state.dropDownsReducer.listPhim;
  });

  const listCinema = useSelector((state) => {
    return state.dropDownsReducer.listCinema;
  });

  const [stateTenPhim] = useState({
    tenPhim: "",
  });

  const [stateRapPhim] = useState({
    tenRapPhim: "",
  });

  const getCinemaDropDowns_ = (maPhim, tenPhim) => {
    stateTenPhim.tenPhim = tenPhim;
    dispatch(getCinemaDropDownsWithCode(maPhim));
  };

  const renderListMovie = () => {
    return listMovie.map((item, index) => {
      return (
        <a
          key={index}
          onClick={() => getCinemaDropDowns_(item.maPhim, item.tenPhim)}
        >
          {item.tenPhim}
        </a>
      );
    });
  };
  const renderListCinema = () => {
    return listCinema.heThongRapChieu.map((item, index) => {
      console.log(item);
      return (
        <section key={index}>
          {item.cumRapChieu.map((items, indexs) => {
            return (
              <a
                key={indexs}
                onClick={getCodeCinema(item.maCumRap, items.tenCumRap)}
              >
                {items.tenCumRap}
              </a>
            );
          })}
        </section>
      );
    });
  };

  const getCodeCinema = (cinema, tenCumRap) => {
    console.log(cinema);
    stateRapPhim.tenRapPhim = tenCumRap;
  };
  return (
    <div className="container dropDownsRelavite">
      <div className="dropDownsMain">
        <div className="dropdownPhim">
          <button className="dropbtn">
            {listCinema && listCinema.heThongRapChieu
              ? stateTenPhim.tenPhim
              : "Ch·ªçn Phim"}
          </button>
          <ExpandMoreIcon />
          <div className="contentPhim">{renderListMovie()}</div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">R·∫°p</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            {listCinema && listCinema.heThongRapChieu ? (
              renderListCinema()
            ) : (
              <a href="#">Ch·ªçn Phim</a>
            )}
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Ng√†y Xem</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Xu·∫•t Chi·∫øu</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            <a href="#">Link 1</a>
            <a href="#">Link 2</a>
            <a href="#">Link 3</a>
          </div>
        </div>

        <button className="btnMuaVe">Mua V√© Ngay</button>
      </div>
    </div>
  );
};
export default DropDowns;
