import { Container } from "@material-ui/core";
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  hidenFormEditClient,
  updateListClientManagement,
} from "../../store/actions/clientManagement.action";
import "./scss/FromEditClient.css";

function FromEditClient(props) {
  const dispatch = useDispatch();
  const infoClient = useSelector((state) => {
    return state.ClientManagementReducer.infoClient;
  });
  console.log(infoClient);
  console.log(props.maNhom);
  const [editClient, setEditClient] = useState({
    taiKhoan: infoClient.taiKhoan,
    matKhau: infoClient.matKhau,
    confirmMatKhau: infoClient.confirmMatKhau,
    email: infoClient.email,
    soDt: infoClient.soDt,
    maNhom: props.maNhom,
    maLoaiNguoiDung: infoClient.maLoaiNguoiDung,
    hoTen: infoClient.hoTen,
  });

  const [validClient, setValidClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau: "",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "KhachHang",
    hoTen: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditClient({ ...editClient, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(updateListClientManagement(editClient));
  };
  const hidenFormEdit = () => {
    dispatch(hidenFormEditClient(false));
  };
  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  return (
    <div className="backgroundFormEdit">
      <div className="wrapFromEdit">
        <Container maxWidth="md">
          <h4>Form Đăng Ký</h4>
          <form onSubmit={handleSubmit}>
            <input
              onChange={handleChange}
              value={editClient.taiKhoan}
              placeholder="Tài Khoản"
              name="taiKhoan"
              type="text"
            />
            <span>{validClient.taiKhoan}</span>
            <input
              onChange={handleChange}
              value={editClient.matKhau}
              placeholder="Mật Khẩu"
              name="matKhau"
              type="password"
            />
            <span>{validClient.matKhau}</span>
            <input
              onChange={handleChange}
              value={editClient.confirmMatKhau}
              placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
              name="confirmMatKhau"
              type="password"
            />
            <span>{validClient.confirmMatKhau}</span>

            <input
              onChange={handleChange}
              value={editClient.hoTen}
              placeholder="Họ Tên"
              name="hoTen"
              type="text"
            />
            <span>{validClient.hoTen}</span>

            <input
              onChange={handleChange}
              value={editClient.email}
              placeholder="Email"
              name="email"
              type="email"
              pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
            />
            <span>{validClient.email}</span>

            <input
              onChange={handleChange}
              value={editClient.soDt}
              placeholder="Số Điện Thoại"
              name="soDt"
            />
            <span>{validClient.soDt}</span>

            <select
              name="maLoaiNguoiDung"
              value={editClient.maLoaiNguoiDung}
              onChange={handleChange}
            >
              <option value="QuanTri">Quản Trị</option>
              <option value="KhachHang">Khách Hàng</option>
            </select>

            <select value={editClient.maNhom}></select>
            <br />
            <span>{validClient.maNhom}</span>

            <br />
            <br />

            <div style={{ textAlign: "center" }}>
              <button
                type="submit"
                style={{ cursor: "pointer" }}
                onClick={hidenFormEdit}
              >
                Cancel
              </button>
              <button type="submit" style={{ cursor: "pointer" }}>
                Update
              </button>
            </div>
          </form>
        </Container>
      </div>
    </div>
  );
}

export default FromEditClient;
