import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Pagination from "@material-ui/lab/Pagination";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import {
  getListClientPagination,
  getListSearchClientPagination,
} from "../../store/actions/listClientPagination";
import "./scss/DemoPaination.css";
import { TablePagination } from "@material-ui/core";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function DemoPaination() {
  const classes = useStyles();
  const dispatch = useDispatch();

  const [stateSearch, setSearch] = useState({
    search: "",
  });
  const [stateMaNhom, setMaNhom] = useState({
    maNhom: "GP01",
  });
  const listUserPagination = useSelector(
    (state) => state.ListClientPaginationReducer.listUserPagination
  );

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(5);
  const handleChangePage = (event, value) => {
    setPage(value);
  };
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 5));
    setPage(0);
  };
  const emptyRows =
    rowsPerPage -
    Math.min(rowsPerPage, listUserPagination.length - page * rowsPerPage);

  useEffect(() => {
    if (stateSearch.search === "") {
      dispatch(getListClientPagination(stateMaNhom.maNhom, page));
    }
  }, [dispatch, stateMaNhom.maNhom, page, stateSearch.search]);

  useEffect(() => {
    if (stateSearch.search !== "") {
      dispatch(
        getListSearchClientPagination(
          stateMaNhom.maNhom,
          stateSearch.search,
          page
        )
      );
    }
  }, [dispatch, stateMaNhom.maNhom, page, stateSearch.search]);

  const handleMaNhom = (e) => {
    setMaNhom({ ...stateMaNhom, maNhom: e.target.value });
    setPage(1);
  };
  const handleSearch = (e) => {
    setSearch({ ...stateSearch, search: e.target.value });
  };

  const renderListClient = () => {
    return listUserPagination
      .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
      .map((item, index) => {
        return (
          <TableRow key={index}>
            <TableCell component="th" scope="row">
              {item.taiKhoan}
            </TableCell>
            <TableCell>{item.matKhau}</TableCell>
            <TableCell>{item.hoTen}</TableCell>
            <TableCell>{item.soDt}</TableCell>
            <TableCell>{item.email}</TableCell>
            <TableCell>{item.maLoaiNguoiDung}</TableCell>
          </TableRow>
        );
      });
  };

  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };
  // maNhom

  return (
    <>
      <div className="demoPagination">
        <span>Mã Nhóm : </span>
        <select
          name="maNhom"
          value={stateMaNhom.maNhom}
          onChange={handleMaNhom}
        >
          {renderMaNhom()}
        </select>
        <input
          className="inputSearch"
          type="text"
          placeholder="Tìm Người Dùng API"
          name="search"
          onChange={handleSearch}
          value={stateSearch.search}
        />
      </div>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Tài Khoản</TableCell>
              <TableCell>Mật Khẩu</TableCell>
              <TableCell>Họ Tên</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Chức Vụ</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {renderListClient()}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={listUserPagination.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
        {/* <Pagination
          count={listUserPagination.length}
          page={page}
          onChange={handleChangePage}
        /> */}
      </TableContainer>
    </>
  );
}
