import { GET_INFO_USER } from "../constants/getInfoUser.const";

const initialState = {
  hoTen: "",
  taiKhoan: "",
  matKhau: "",
  email: "",
  soDT: "",
  loaiNguoiDung: "",
  maNhom: "",
  thongTinDatVe: [],

};

const infoUserReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case GET_INFO_USER:
      const { thongTinDatVe, ..._payload } = payload
      state.thongTinDatVe = thongTinDatVe
      state = payload;
      console.log(state);
      return { ...state };
    default:
      return state;
  }
};
export default infoUserReducer;
