import { GET_INFO_CLIENT, GET_LIST_CLIENT_MANAGEMENT, GET_LIST_SEARCH_CLIENT_MANAGEMENT, PAGE_EDIT_CLIENT, TAIKHOAN_CLIENT_MANAGEMENT, UPDATE_LIST_CLIENT_MANAGEMENT } from "../constants/clientManagement.constant"

const initialState = {
    listClient: [],
    pageFormEdit: false,
    infoClient: {},
}

export const ClientManagementReducer = (state = initialState, { type, payload }) => {
    let listClientUpdate = [...state.listClient]
    switch (type) {
        case GET_LIST_CLIENT_MANAGEMENT:
            state.listClient = payload
            return { ...state, ...payload }
        case GET_LIST_SEARCH_CLIENT_MANAGEMENT: {
            listClientUpdate = payload
            state.listClient = listClientUpdate
            return { ...state }
        }
        case TAIKHOAN_CLIENT_MANAGEMENT: {
            let index = listClientUpdate.findIndex(item => item.taiKhoan === payload)
            if (index !== -1) {
                listClientUpdate.splice(index, 1)
            }
            state.listClient = listClientUpdate
            return { ...state }
        }
        case PAGE_EDIT_CLIENT: {
            state.pageFormEdit = payload;
            return { ...state }
        }
        case GET_INFO_CLIENT: {
            state.infoClient = payload
            return { ...state }
        }
        case UPDATE_LIST_CLIENT_MANAGEMENT: {
            console.log(payload);
            let index = listClientUpdate.find(item => item.taiKhoan === payload.taiKhoan)
            if (index !== -1) {
                listClientUpdate[index] = payload
            }
            console.log(listClientUpdate);
            state.listClient = listClientUpdate

            return { ...state }
        }
        default:
            return state
    }
}
