import React from "react";
// redux hook
import { useDispatch } from "react-redux";
// redux thunk action
import { signOut_Action } from "../../store/actions/signIn.action";
//react-router
import { useHistory } from "react-router";
// material-ui
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import AccountCircleIcon from "@material-ui/icons/AccountCircle";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import SupervisorAccountIcon from "@material-ui/icons/SupervisorAccount";
// css
import { withStyles } from "@material-ui/core/styles";
import "./css/infoUser.style.css";

// style component
const StyledMenu = withStyles({
  paper: {
    border: "1px solid #d3d4d5",
  },
})((props) => (
  <Menu
    elevation={0}
    getContentAnchorEl={null}
    anchorOrigin={{
      vertical: "bottom",
      horizontal: "center",
    }}
    transformOrigin={{
      vertical: "top",
      horizontal: "center",
    }}
    {...props}
  />
));
// style component
const StyledMenuItem = withStyles((theme) => ({
  root: {
    "&:focus": {
      "& .MuiListItemIcon-root, & .MuiListItemText-primary": {
        color: theme.palette.common.white,
      },
    },
  },
}))(MenuItem);

function InfoUser(props) {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };
  const dispatch = useDispatch();
  // get data to localstorage have save form action/signIn_Action
  const maLoaiNguoiDung = JSON.parse(localStorage.getItem("maLoaiNguoiDung"));
  const userLogin = JSON.parse(localStorage.getItem("userLogin"));
  const history = useHistory();
  const logOut = () => {
    localStorage.clear();
    history.push("/signIn");
    dispatch(signOut_Action()); // post action to action/signIn.action
    setAnchorEl(null);
  };
  const showProfile = () => {
    setAnchorEl(null);
    history.push("/profile");
  };
  const pageAdmin = () => {
    setAnchorEl(null);
    history.push("/admin");
  };
  return (
    <>
      <p className="textSignOut">
        Hi {userLogin && userLogin.hoTen} !
        <ExpandMoreIcon
          className="iconSignOut"
          onClick={handleClick}
          style={{ verticalAlign: "middle", cursor: "pointer" }}
        />
      </p>
      <StyledMenu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        {maLoaiNguoiDung === "QuanTri" ? (
          <StyledMenuItem onClick={pageAdmin}>
            <SupervisorAccountIcon className="iconSignOut" />
            <label className="textSignOut"> Admin</label>
          </StyledMenuItem>
        ) : (
          ""
        )}
        <StyledMenuItem onClick={showProfile}>
          <AccountCircleIcon className="iconSignOut" />
          <label className="textSignOut"> Profile</label>
        </StyledMenuItem>
        <StyledMenuItem onClick={logOut}>
          <ExitToAppIcon className="iconSignOut" />
          <label className="textSignOut"> Log Out</label>
        </StyledMenuItem>
      </StyledMenu>
    </>
  );
}

export default InfoUser;
