import React from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import PersonPinIcon from "@material-ui/icons/PersonPin";
import ShoppingBasket from "@material-ui/icons/ShoppingBasket";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getInfoUserAction } from "../../store/actions/getInfoUser.action";
import "./css/profile.scss";
import "./css/profile.css";
import "../../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import { Button } from "@material-ui/core";

// import HelpIcon from "@material-ui/icons/Help";
// import ThumbDown from "@material-ui/icons/ThumbDown";
// import ThumbUp from "@material-ui/icons/ThumbUp";
// import PhoneIcon from "@material-ui/icons/Phone";
// import FavoriteIcon from "@material-ui/icons/Favorite";
// import { red } from "@material-ui/core/colors";
// import CreateIcon from "@material-ui/icons/Create";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`scrollable-prevent-tabpanel-${index}`}
      aria-labelledby={`scrollable-prevent-tab-${index}`}
      {...other}
    >
      <div className="menu"></div>
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `scrollable-prevent-tab-${index}`,
    "aria-controls": `scrollable-prevent-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    width: "100%",
    // backgroundColor: theme.palette.background.paper,
    // backgroundColor: red,
  },
}));

export default function ProfileUser() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const taiKhoan = JSON.parse(localStorage.getItem("taiKhoan"));
  const maLoaiNguoiDung = JSON.parse(localStorage.getItem("maLoaiNguoiDung"));

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getInfoUserAction(taiKhoan));
  }, [dispatch, taiKhoan]);

  const user = useSelector((state) => state.infoUserReducer);
  return (
    <div className="container">
      <div className={"root"}>
        <AppBar position="static" className="menu">
          <Tabs
            value={value}
            onChange={handleChange}
            variant="scrollable"
            scrollButtons="off"
            aria-label="scrollable prevent tabs example"
          >
            <Tab
              icon={<PersonPinIcon />}
              aria-label="person"
              {...a11yProps(2)}
            />

            <Tab
              icon={<ShoppingBasket />}
              aria-label="shopping"
              {...a11yProps(4)}
            />
          </Tabs>
        </AppBar>
        <TabPanel value={value} index={0}>
          <h3 style={{ textAlign: "center", margin: "0 0 10px 0" }}>
            THÔNG TIN NGƯỜI DÙNG
          </h3>
          <table className="table">
            <thead>
              <tr>
                {/* <th scope="col">#</th>
                <th scope="col">First</th> */}
              </tr>
            </thead>
            <tbody>
              <tr>
                <th scope="row">Họ tên: </th>
                <td>{user.hoTen}</td>
              </tr>{" "}
              <tr>
                <th scope="row">Email: </th>
                <td>{user.email}</td>
              </tr>{" "}
              <tr>
                <th scope="row">Số điện thoại: </th>
                <td>{user.soDT}</td>
              </tr>{" "}
              <tr>
                <th scope="row">Tài khoản: </th>
                <td>{user.taiKhoan}</td>
              </tr>
              <tr>
                <th scope="row">Mật khẩu: </th>
                <td>*******</td>
              </tr>
              <tr>
                <th scope="row">Vai trò: </th>
                <td>
                  {maLoaiNguoiDung === "QuanTri"
                    ? "Quản trị viên"
                    : "Khách hàng"}
                </td>
              </tr>
            </tbody>
            <Button
              variant="outlined"
              style={{
                width: "auto",
                border: "1px solid white",
                color: "#fa5238",
              }}
              id="btnUpdate"
            >
              {"Cập nhật thông tin "}
              {/* <CreateIcon /> */}
            </Button>
          </table>
        </TabPanel>
        <TabPanel value={value} index={1}>
          Item Two
        </TabPanel>
      </div>
    </div>
  );
}
