import { ERR_NOTIFI_SIGNUP, OFF_ERR_NOTIFI_SIGNUP, SUCCESS_NOTIFI_SIGNUP } from "../constants/signup.constant"

const initialState = {
    messageSignUp: "",
    mesageSuccess: "",
}

export const SignUpReducer = (state = initialState, { type, payload }) => {

    switch (type) {

        case ERR_NOTIFI_SIGNUP:
            state.messageSignUp = payload
            return { ...state, ...payload }
        case SUCCESS_NOTIFI_SIGNUP:
            state.mesageSuccess = payload
            return { ...state, ...payload }
        case OFF_ERR_NOTIFI_SIGNUP:
            let update = { ...state.messageSignUp }
            update = payload
            state.messageSignUp = update
            return { ...state }
        default:
            return state
    }
}
