import React, { Component } from "react";
// action redux thunk
import { connect } from "react-redux";
import { postSignUp_Action } from "../../store/actions/signUp.action";
// material
import { Container } from "@material-ui/core";
import "./scss/signup.css";

class SignUpPage extends Component {
  state = {
    User: {
      taiKhoan: "",
      matKhau: "",
      email: "",
      soDt: "",
      maNhom: "GP01",
      maLoaiNguoiDung: "KhachHang",
      hoTen: "",
    },
    error: {
      taiKhoan: "",
      matKhau: "",
      email: "",
      soDt: "",
      hoTen: "",
    },
    messageSignUp: "",
  };

  handleChange = (event) => {
    const { name, value } = event.target;
    let valuesUpdate = { ...this.state.User, [name]: value };
    let errorUpdate = { ...this.state.error, [name]: value };
    if (value.trim() === "") {
      errorUpdate[name] = " Do Not Empty ! ";
    } else {
      errorUpdate[name] = "";
    }
    this.setState({
      User: valuesUpdate,
      error: errorUpdate,
    });
  };

  test = () => {
    if (this.props.messageSignUp) {
      this.setState({
        messageSignUp: this.props.messageSignUp,
      });
    } else {
      this.setState({
        messageSignUp: "",
      });
    }
  };

  handleSubmit = (event) => {
    console.log(this.props.messageSignUp);
    event.preventDefault();
    this.props.dispatch(postSignUp_Action(this.state.User));
  };
  refeshInput = () => {
    let elementsInput = document.querySelectorAll("input");
    for (let refesh of elementsInput) {
      refesh.value = "";
    }
  };

  componentDidUpdate() {
    if (this.props.mesageSuccess) {
      this.props.history.replace("/signIn");
    }
  }
  render() {
    const { User, error } = this.state;
    return (
      <div className="backgroundSignUp">
        <div className="wrapSignUp">
          <Container maxWidth="md">
            <h4>Form Đăng Ký</h4>
            <form onSubmit={this.handleSubmit}>
              <input
                onChange={this.handleChange}
                value={User.taiKhoan}
                placeholder="Tài Khoản"
                name="taiKhoan"
              />
              <span>{error.taiKhoan}</span>
              <input
                onChange={this.handleChange}
                value={User.matKhau}
                placeholder="Mật Khẩu"
                name="matKhau"
              />
              <span>{error.matKhau}</span>

              <input
                onChange={this.handleChange}
                value={User.email}
                placeholder="Email"
                name="email"
              />
              <span>{error.email}</span>

              <input
                onChange={this.handleChange}
                value={User.soDt}
                placeholder="Số Điện Thoại"
                name="soDt"
              />
              <span>{error.soDt}</span>

              <input
                onChange={this.handleChange}
                value={User.hoTen}
                placeholder="User Name"
                name="hoTen"
              />
              <span>{error.hoTen}</span>
              <br />
              <br />
              <span>{this.state.messageSignUp}</span>
              <div style={{ textAlign: "center" }}>
                <button type="submit">Đăng Ký</button>
              </div>
            </form>
          </Container>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    messageSignUp: state.SignUpReducer.messageSignUp,
    mesageSuccess: state.SignUpReducer.mesageSuccess,
  };
};

export default connect(mapStateToProps)(SignUpPage);
