import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TablePagination from "@material-ui/core/TablePagination";
import Paper from "@material-ui/core/Paper";
import { useEffect } from "react";
import { getListClientManagement } from "../../store/actions/clientManagement.action";

const useStyles = makeStyles({
  table: {
    maxWidth: "100%",
  },
});

const ClientMaragement = () => {
  const classes = useStyles();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getListClientManagement());
  }, [dispatch]);

  const listClient = useSelector((state) => {
    return state.ClientManagementReducer.listClient;
  });
  console.log(listClient);

  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };
  const emptyRows =
    rowsPerPage - Math.min(rowsPerPage, listClient.length - page * rowsPerPage);

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell align="center">Tài Khoản</TableCell>
            <TableCell align="center">Mật Khẩu</TableCell>
            <TableCell align="center">Họ Tên</TableCell>
            <TableCell align="center">Email</TableCell>
            <TableCell align="center">Số Điện Thoại</TableCell>
            <TableCell align="center">Chức Vụ</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {listClient
            .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
            .map((item, index) => (
              <TableRow key={index}>
                <TableCell align="center">{item.taiKhoan}</TableCell>
                <TableCell align="center">{item.matKhau}</TableCell>
                <TableCell align="center">{item.hoTen}</TableCell>
                <TableCell align="center">{item.email}</TableCell>
                <TableCell align="center">{item.soDt}</TableCell>
                <TableCell align="center">{item.maLoaiNguoiDung}</TableCell>
              </TableRow>
            ))}
          {emptyRows > 0 && (
            <TableRow style={{ height: 53 * emptyRows }}>
              <TableCell colSpan={6} />
            </TableRow>
          )}
        </TableBody>
      </Table>
      <TablePagination
        rowsPerPageOptions={[5, 10, 25]}
        component="div"
        count={listClient.length}
        rowsPerPage={rowsPerPage}
        page={page}
        onChangePage={handleChangePage}
        onChangeRowsPerPage={handleChangeRowsPerPage}
      />
    </TableContainer>
  );
};
export default ClientMaragement;
