import { Container } from "@material-ui/core";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import {
  addClientManagement,
  hidenFormClient,
} from "../../store/actions/clientManagement.action";
import "./scss/FromAddClient.css";

function FromAddClient() {
  const dispatch = useDispatch();

  const [stateAddClient, setAddClient] = useState({
    user: {
      taiKhoan: "",
      matKhau: "",
      confirmMatKhau: "",
      email: "",
      soDt: "",
      maNhom: "",
      maLoaiNguoiDung: "",
      hoTen: "",
    },
    error: {
      taiKhoan: "",
      matKhau: "",
      confirmMatKhau: "",
      email: "",
      soDt: "",
      maNhom: "",
      maLoaiNguoiDung: "",
      hoTen: "",
    },
    validSubmit: false,
  });

  const handleChange = (e) => {
    const { name, value, pattern, type } = e.target;

    let valueUpdate = { ...stateAddClient.user, [name]: value };
    let errorUpdate = { ...stateAddClient.error, [name]: value };

    setAddClient(
      {
        user: valueUpdate,
        error: errorUpdate,
      },
      () => {
        validButtonSubmit();
      }
    );
    // check pattern
    let regex = new RegExp(pattern);
    // valid check empty
    if (value.trim() === "") {
      errorUpdate[name] = "Do Not Empty";
    } else {
      errorUpdate[name] = "";
    }
    // valid check empty
    // if (name === "maNhom") {
    //   if (!value) {
    //     errorUpdate[name] = "Chọn Mã Nhóm";
    //   }
    // }
    // if (name === "maLoaiNguoiDung") {
    //   if (!value) {
    //     errorUpdate[name] = "Chọn Mã Người Dùng";
    //   }
    // }
    // valid check empty

    // check password
    if (name === "confirmMatKhau") {
      if (value === valueUpdate["matKhau"]) {
        errorUpdate[name] = "";
      } else {
        errorUpdate[name] = "Mật Khẩu Không Trùng Nhau !";
      }
    }
    // check password

    // check email
    if (type === "email") {
      if (!value) {
        errorUpdate[name] = "Do Not Empty !";
      } else if (!regex.test(value)) {
        errorUpdate[name] = "Email Invaild !";
      }
    }
    // check email
  };

  const validButtonSubmit = () => {
    let valid = true;
    for (let key in stateAddClient.error) {
      if (stateAddClient.error[key] !== "" || stateAddClient.user[key] === "") {
        valid = false;
      }
    }
    setAddClient({
      ...stateAddClient,
      validSubmit: valid,
    });
  };
  console.log(stateAddClient.user);
  console.log(stateAddClient.error);
  console.log(stateAddClient.validSubmit);
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(addClientManagement(stateAddClient.user));
    dispatch(hidenFormClient("listUser"));
  };
  const hidenFormEdit = () => {
    dispatch(hidenFormClient("listUser"));
  };

  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  return (
    <div className="backgroundFormAdd">
      <div className="wrapFromAdd">
        <Container maxWidth="md">
          <h4>Thêm Người Dùng</h4>
          <form onSubmit={handleSubmit}>
            <input
              onChange={handleChange}
              value={stateAddClient.user.taiKhoan}
              placeholder="Tài Khoản"
              name="taiKhoan"
              type="text"
            />
            <span>{stateAddClient.eror.taiKhoan}</span>

            <input
              onChange={handleChange}
              value={stateAddClient.user.matKhau}
              placeholder="Mật Khẩu"
              name="matKhau"
              type="password"
            />
            <span>{stateAddClient.eror.matKhau}</span>

            <input
              onChange={handleChange}
              value={stateAddClient.user.confirmMatKhau}
              placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
              name="confirmMatKhau"
              type="password"
            />
            <span>{stateAddClient.eror.confirmMatKhau}</span>

            <input
              onChange={handleChange}
              value={stateAddClient.user.hoTen}
              placeholder="Họ Tên"
              name="hoTen"
              type="text"
            />
            <span>{stateAddClient.eror.hoTen}</span>

            <input
              onChange={handleChange}
              value={stateAddClient.user.email}
              placeholder="Email"
              name="email"
              type="email"
              pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
            />
            <span>{stateAddClient.eror.email}</span>

            <input
              onChange={handleChange}
              value={stateAddClient.user.soDt}
              placeholder="Số Điện Thoại"
              name="soDt"
            />
            <span>{stateAddClient.eror.soDt}</span>
            <br />

            <select
              name="maLoaiNguoiDung"
              value={stateAddClient.user.maLoaiNguoiDung}
              onChange={handleChange}
            >
              <option value="">Loại Người Dùng</option>
              <option value="QuanTri">Quản Trị</option>
              <option value="KhachHang">Khách Hàng</option>
            </select>

            <select
              name="maNhom"
              value={stateAddClient.user.maNhom}
              onChange={handleChange}
            >
              <option value="">Mã Nhóm</option>
              {renderMaNhom()}
            </select>
            <br />
            <span style={{ marginRight: "20px" }}>
              {stateAddClient.eror.maLoaiNguoiDung}
            </span>
            <span>{stateAddClient.eror.maNhom}</span>

            <div style={{ textAlign: "center" }}>
              <button
                type="button"
                style={{ cursor: "pointer", color: "red" }}
                onClick={hidenFormEdit}
              >
                Cancel
              </button>
              {stateAddClient.validSubmit ? (
                <button type="submit" style={{ cursor: "pointer" }}>
                  Add
                </button>
              ) : (
                <button disabled style={{ cursor: "no-drop" }}>
                  Add
                </button>
              )}
            </div>
          </form>
        </Container>
      </div>
    </div>
  );
}

export default FromAddClient;
