import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Pagination from "@material-ui/lab/Pagination";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import { getListClientPagination } from "../../store/actions/listClientPagination";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

export default function DemoPaination() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [page, setPage] = React.useState(1);

  const [stateMaNhom, setMaNhom] = useState({
    maNhom: "GP01",
  });
  const handleChange = (event, value) => {
    setPage(value);
  };

  useEffect(() => {
    dispatch(getListClientPagination(stateMaNhom.maNhom, page));
  }, [dispatch, stateMaNhom.maNhom, page]);

  const listUserPagination = useSelector(
    (state) => state.ListClientPaginationReducer.listUserPagination
  );

  const handleMaNhom = (e) => {
    setMaNhom({ ...stateMaNhom, maNhom: e.target.value });
  };
  console.log(stateMaNhom.maNhom);
  const renderListClient = () => {
    return listUserPagination.map((item, index) => {
      return (
        <TableRow key={index}>
          <TableCell component="th" scope="row">
            {item.taiKhoan}
          </TableCell>
          <TableCell>{item.matKhau}</TableCell>
          <TableCell>{item.hoTen}</TableCell>
          <TableCell>{item.soDt}</TableCell>
          <TableCell>{item.email}</TableCell>
          <TableCell>{item.maLoaiNguoiDung}</TableCell>
        </TableRow>
      );
    });
  };

  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };
  // maNhom

  return (
    <>
      <span>Mã Nhóm : </span>
      <select name="maNhom" value={stateMaNhom.maNhom} onChange={handleMaNhom}>
        {renderMaNhom()}
      </select>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Tài Khoản</TableCell>
              <TableCell>Mật Khẩu</TableCell>
              <TableCell>Họ Tên</TableCell>
              <TableCell>Phone</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Chức Vụ</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>{renderListClient()}</TableBody>
        </Table>
        <div style={{ textAlign: "center" }}>
          <Pagination count={5} page={page} onChange={handleChange} />
        </div>
      </TableContainer>
    </>
  );
}
