import React, { useEffect, useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import TablePagination from "@material-ui/core/TablePagination";
import Paper from "@material-ui/core/Paper";
import "./scss/MovieManagement.css";
import { useDispatch, useSelector } from "react-redux";
import { getListMovieManagement } from "../../store/actions/movieManagement.action";

import format from "date-format";

const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});
function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}

const rows = [
  createData("Frozen yoghurt", 159, 6.0, 24, 4.0),
  createData("Ice cream sandwich", 237, 9.0, 37, 4.3),
  createData("Eclair", 262, 16.0, 24, 6.0),
  createData("Cupcake", 305, 3.7, 67, 4.3),
  createData("Gingerbread", 356, 16.0, 49, 3.9),
  createData("Gingerbread2", 356, 16.0, 49, 3.9),
  createData("Gingerbread3", 356, 16.0, 49, 3.9),
  createData("Gingerbread4", 356, 16.0, 49, 3.9),
  createData("Gingerbread5", 356, 16.0, 49, 3.9),
  createData("Gingerbread6", 356, 16.0, 49, 3.9),
  createData("Gingerbread7", 356, 16.0, 49, 3.9),
  createData("Gingerbread8", 356, 16.0, 49, 3.9),
  createData("Gingerbread9", 356, 16.0, 49, 3.9),
  createData("Gingerbread10", 356, 16.0, 49, 3.9),
  createData("Gingerbread11", 356, 16.0, 49, 3.9),
  createData("Gingerbread12", 356, 16.0, 49, 3.9),
  createData("Gingerbread13", 356, 16.0, 49, 3.9),
];

export default function MovieManagement() {
  const classes = useStyles();
  const dispatch = useDispatch();

  const [stateAddMovie, setStateAddMovie] = useState({
    search: "",
    maNhom: "GP01",
  });

  const movieList = useSelector((state) => {
    return state.MovieManagementReducer.movieList;
  });

  console.log(movieList);
  useEffect(() => {
    dispatch(getListMovieManagement(stateAddMovie.maNhom));
  }, [stateAddMovie.maNhom, dispatch]);

  // table
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(10);
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };
  const emptyRows =
    rowsPerPage - Math.min(rowsPerPage, movieList.length - page * rowsPerPage);
  // table

  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStateAddMovie({ ...stateAddMovie, [name]: value });
  };
  const submitSearchList = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <form onSubmit={submitSearchList} className="formMovie">
        <h5>Danh Sách Người Dùng</h5>
        <span>Mã Nhóm : </span>
        <select
          name="maNhom"
          value={stateAddMovie.maNhom}
          onChange={handleChange}
        >
          {renderMaNhom()}
        </select>
        <button type="button">Thêm Phim</button>
        <input
          className="inputSearch"
          type="text"
          placeholder="Tìm Phim"
          name="search"
          onChange={handleChange}
          value={stateAddMovie.search}
        />
      </form>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Mã Phim</TableCell>
              <TableCell>Tên Phim</TableCell>
              <TableCell>Ngày Chiếu</TableCell>
              <TableCell>Nhóm</TableCell>
              <TableCell>Đánh Giá</TableCell>
              <TableCell>Bí Danh</TableCell>
              <TableCell>Hình Ảnh</TableCell>
              <TableCell>Trailer</TableCell>
              <TableCell>Mô Tả</TableCell>
              <TableCell>Cài Đặt</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {movieList
              .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
              .map((item, index) => (
                <TableRow key={index}>
                  <TableCell>{item.maPhim}</TableCell>
                  <TableCell>{item.tenPhim}</TableCell>
                  <TableCell>
                    {format("hh:mm - mm-dd-yyyy", new Date(item.ngayKhoiChieu))}
                  </TableCell>
                  <TableCell>{item.maNhom}</TableCell>
                  <TableCell>{item.danhGia}</TableCell>
                  <TableCell>{item.biDanh}</TableCell>
                  <TableCell>
                    <img
                      src={item.hinhAnh}
                      alt={item.hinhAnh}
                      style={{ width: "50px", height: "50px" }}
                    />
                  </TableCell>
                  <TableCell>{item.trailer.slice(0, 20)}</TableCell>
                  <TableCell>{item.moTa.slice(0, 20)}</TableCell>
                  <TableCell>x</TableCell>
                </TableRow>
              ))}
            {emptyRows > 0 && (
              <TableRow style={{ height: 53 * emptyRows }}>
                <TableCell colSpan={6} />
              </TableRow>
            )}
          </TableBody>
        </Table>
        <TablePagination
          rowsPerPageOptions={[5, 10, 25]}
          component="div"
          count={movieList.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </TableContainer>
    </>
  );
}
