import Axios from "axios";
import { DOMAIN } from "../../services/domainUrl";
import { GET_LIST_CLIENT_MANAGEMENT, GET_LIST_SEARCH_CLIENT_MANAGEMENT } from "../constants/clientManagement.constant";

export const getListClientManagement = (maNhom) => {
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "GET",
                url: `${DOMAIN}QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=GP01`
            })
            dispatch({
                type: GET_LIST_CLIENT_MANAGEMENT,
                payload: res.data
            })
        } catch (error) {
            console.log(error);
        }
    }
}

export const getListSearchClientManagement = (tuKhoa) => {
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "GET",
                url: `${DOMAIN}QuanLyNguoiDung/LayDanhSachNguoiDung?MaNhom=GP01&tuKhoa=${tuKhoa}`
            })
            dispatch({
                type: GET_LIST_SEARCH_CLIENT_MANAGEMENT,
                payload: res.data
            })
        } catch (error) {
            console.log(error);
        }
    }
}
