import { GET_MOVIE_DETAIL } from "../constants/detail.constant"
import { GET_LOGO_DETAILTAB } from "../constants/detailTabComponent.const"

const initialState = {
    listLogoDetail: [],
    detailMovie: {},
}

export const DetailTabReducer = (state = initialState, action) => {
    const { type } = action
    switch (type) {
        case GET_LOGO_DETAILTAB: {
            state.listShowTimeDetail = action.payload
            return { ...state }
        }
        case GET_MOVIE_DETAIL: {
            state.detailMovie = action.payload;
            return { ...state };
          }
        default:
            return state
    }
}
