import React, { useEffect, useState } from "react";
import "./scss/movieDetail.css";
// material ui
import { Grid, makeStyles } from "@material-ui/core";
import StarIcon from "@material-ui/icons/Star";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
// styled materiall
// redux
import { useDispatch, useSelector } from "react-redux";
// reduer action
import { getMovieDetail_Action } from "../../store/actions/detail.actions";
import { getInforSystem } from "../../store/actions/tabNavigationComponent.action";
import { withRouter } from "react-router";
import Loader from "../Loader/Loader";
import format from "date-format";
import Container from "@material-ui/core/Container";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  control: {
    padding: theme.spacing(2),
  },
}));

function MovieDetail(props) {

  const classes = useStyles();

  const dispatch = useDispatch();
  const [loading, setLoading] = useState(null);

  const codeMaPhim = props.match.params.maPhim; // recive data to listFlim/Listphim.page
  const detail = useSelector((state) => {
    return state.detaiMovielReducer.detail || {};
  });
  console.log(detail);
  useEffect(() => {
    dispatch(getMovieDetail_Action(codeMaPhim)).then((e) => setLoading(e)); // post data (codeMaPhim === id) to Axios detail.action & return object to detaiMovielReducer
  }, [dispatch, codeMaPhim]);
  useEffect(() => {
    dispatch(getInforSystem()); // get data to Axios tabNavigation.action for component NavigationTabsOne
  }, [dispatch]);
  return (
    <>
      {loading === null ? (
        <Loader style={{ textAlign: "center" }} />
      ) : (
        <div className="movieDetail">
          <div className="movieDetail_bg">
            <img src={detail.hinhAnh} alt="" />
          </div>

          <div className="movieDetail_Intro">
            <Container fixed>
              <Grid container className={classes.control}>
                <Grid item xs={12} md={4} lg={2}>
                  <img src={detail.hinhAnh} alt="" />
                </Grid>
                <Grid item xs={12} md={4} lg={6} className="intro">
                  <label>
                    Ngày Chiếu :{" "}
                    {format("mm-dd-yyyy", new Date(detail.ngayKhoiChieu))}
                  </label>
                  <p>
                    <span>{detail.maPhim}</span> Phim : {detail.tenPhim}
                  </p>
                  <label>Mô Tả : {detail.moTa}</label>
                </Grid>
                <Grid item xs={12} md={4} lg={4} className="rating">
                  <CircularProgressbar
                    background={true}
                    value={detail.danhGia}
                    text={detail.danhGia}
                    maxValue={10}
                    strokeWidth={6}
                    styles={buildStyles({
                      strokeLinecap: "butt",
                      textColor: "white",
                      pathColor: "#7ed321",
                      trailColor: "#3a3a3a",
                      textSize: "40px",
                      backgroundColor: "rgba(0,0,0,.4)",
                    })}
                  />
                  <p>
                    <StarIcon />
                    <StarIcon />
                    <StarIcon />
                    <StarIcon />
                    <StarIcon />
                  </p>
                  <span>{detail.danhGia} Người Đánh Giá</span>
                </Grid>
              </Grid>
            </Container>
          </div>
        </div>
      )}
    </>
  );
}

export default withRouter(MovieDetail);

