import Axios from "axios"
import { DOMAIN } from "../../services/urlMovie";
import { GET_LIST_MOVIE_DROPDOWNS } from "../constants/dropdown.constant";

export const getListMovieDropDowns = () => {
    return async (dispatch) => {
        try {
            const res = await Axios({
                method: "GET",
                url: `${DOMAIN}/QuanLyPhim/LayDanhSachPhim?maNhom=GP10`
            });
            dispatch({
                type: GET_LIST_MOVIE_DROPDOWNS,
                payload: res.data
            })
        } catch (error) {
            console.log(error);
        }
    }
}
