import { Container } from "@material-ui/core";
import React, { useState } from "react";

function FromEditClient(props) {
  const [editClient, setEditClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau: "",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "KhachHang",
    hoTen: "",
  });

  const [validClient, setValidClient] = useState({
    taiKhoan: "",
    matKhau: "",
    confirmMatKhau: "",
    email: "",
    soDt: "",
    maNhom: "",
    maLoaiNguoiDung: "KhachHang",
    hoTen: "",
  });
  const handleChange = (e) => {
    const { name, value } = e.target.value;
    setEditClient({ ...editClient, [name]: value });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
  };

  // maNhom
  const renderMaNhom = () => {
    let arrMaNhom = [
      "GP01",
      "GP02 ",
      "GP03",
      "GP04 ",
      "GP05",
      "GP06 ",
      "GP07",
      "GP08 ",
      "GP09",
      "GP10 ",
    ];
    return arrMaNhom.map((item, index) => {
      return (
        <option key={index} value={item}>
          {item}
        </option>
      );
    });
  };

  return (
    <Container maxWidth="md">
      <h4>Form Đăng Ký</h4>
      <form onSubmit={handleSubmit}>
        <input
          onChange={handleChange}
          value={editClient.taiKhoan}
          placeholder="Tài Khoản"
          name="taiKhoan"
          type="text"
        />
        <span>{validClient.taiKhoan}</span>
        <input
          onChange={handleChange}
          value={editClient.matKhau}
          placeholder="Mật Khẩu"
          name="matKhau"
          type="password"
        />
        <span>{validClient.matKhau}</span>
        <input
          onChange={handleChange}
          value={editClient.confirmMatKhau}
          placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
          name="confirmMatKhau"
          type="password"
        />
        <span>{validClient.confirmMatKhau}</span>

        <input
          onChange={handleChange}
          value={editClient.hoTen}
          placeholder="editClient Name"
          name="hoTen"
          type="text"
          pattern="^[A-Za-z]+$"
        />
        <span>{validClient.hoTen}</span>

        <input
          onChange={handleChange}
          value={editClient.email}
          placeholder="Email"
          name="email"
          type="email"
          pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
        />
        <span>{validClient.email}</span>

        <input
          onChange={handleChange}
          value={editClient.soDt}
          placeholder="Số Điện Thoại"
          name="soDt"
        />
        <span>{validClient.soDt}</span>

        <input placeholder="Khách Hàng" disabled />

        <select name="maNhom" value={editClient.maNhom} onChange={handleChange}>
          <option value="">Mã Nhóm</option>
          {renderMaNhom()}
        </select>
        <br />
        <span>{validClient.maNhom}</span>

        <br />
        <br />

        <div style={{ textAlign: "center" }}>
          <button
            type="submit"
            style={{ cursor: "pointer" }}
            onClick={() => {
              props.pageEdit(false);
            }}
          >
            Cancel
          </button>
          <button type="submit" style={{ cursor: "pointer" }}>
            Đăng Ký
          </button>
        </div>
      </form>
    </Container>
  );
}

export default FromEditClient;
