/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from "react";
import "./scss/dropdowns.css";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import { useDispatch, useSelector } from "react-redux";
import {
  getListMovieDropDowns,
  getCinemaDropDownsWithCode,
  getDateDropDowns,
} from "../../store/actions/dropdowns.action";
// format dd-mm-yy
import format from "date-format";

const DropDowns = () => {
  const dispatch = useDispatch();
  // call api
  useEffect(() => {
    dispatch(getListMovieDropDowns());
  }, [dispatch]);
  // get data reducer
  const listMovie = useSelector((state) => {
    return state.dropDownsReducer.listPhim;
  });
  // get data reducer
  const listCinema = useSelector((state) => {
    return state.dropDownsReducer.listCinema;
  });

  // get data reducer
  const listLichChieu = useSelector((state) => {
    return state.dropDownsReducer.listLichChieu;
  });

  // get data reducer
  const listXuatChieu = useSelector((state) => {
    return state.dropDownsReducer.listLichChieu;
  });

  // show phim
  const [stateTenPhim] = useState({
    tenPhim: "",
  });
  // show phim
  const getCinemaDropDowns_ = (maPhim, tenPhim) => {
    stateTenPhim.tenPhim = tenPhim;
    dispatch(getCinemaDropDownsWithCode(maPhim));
  };
  // show phim
  const renderListMovie = () => {
    return listMovie.map((item, index) => {
      return (
        <a
          key={index}
          onClick={() => getCinemaDropDowns_(item.maPhim, item.tenPhim)}
        >
          {item.tenPhim}
        </a>
      );
    });
  };

  // show tenRap
  const renderListCinema = () => {
    return listCinema.heThongRapChieu.map((item, index) => {
      return (
        <section key={index}>
          {item.cumRapChieu.map((items, indexs) => {
            return (
              <a
                key={indexs}
                onClick={() => {
                  getCodeCinema(items, items.tenCumRap);
                }}
              >
                {items.tenCumRap}
              </a>
            );
          })}
        </section>
      );
    });
  };

  // show tenRap
  const [stateTenRap, setTenRap] = useState({
    tenRap: "",
  });
  // show tenRap
  const getCodeCinema = (items, tenCumRap) => {
    setTenRap({
      tenRap: tenCumRap,
    });
    dispatch(getDateDropDowns(items));
  };

  // show date
  const [stateDate, setDate] = useState({
    date: "",
  });
  // show date
  const renderDateCinema = () => {
    return listLichChieu.lichChieuPhim.map((item, index) => {
      return (
        <a
          key={index}
          onClick={() => {
            getDateCinema(item.ngayChieuGioChieu);
          }}
        >
          {" "}
          {format("dd-mm-yyyy", new Date(item.ngayChieuGioChieu))}
        </a>
      );
    });
  };
  // show timer
  const getDateCinema = (ngayChieuGioChieu) => {
    let formatDate = format("dd-mm-yyyy", new Date(ngayChieuGioChieu));
    setDate({
      date: formatDate,
    });
  };

  // show timer
  const [stateTimer, setTimer] = useState({
    timer: "",
  });
  // show date
  const renderTimerCinema = () => {
    return listXuatChieu.lichChieuPhim.map((item, index) => {
      return (
        <a
          key={index}
          onClick={() => {
            getTimerCinema(item.ngayChieuGioChieu);
          }}
        >
          {" "}
          {format("hh:mm:ss", new Date(item.ngayChieuGioChieu))}
        </a>
      );
    });
  };
  // show timer
  const getTimerCinema = (ngayChieuGioChieu) => {
    let formatTimer = format("dd-mm-yyyy", new Date(ngayChieuGioChieu));
    setTimer({
      timer: formatTimer,
    });
  };

  return (
    <div className="container dropDownsRelavite">
      <div className="dropDownsMain">
        <div className="dropdownPhim">
          <button className="dropbtn">
            {listCinema.heThongRapChieu ? stateTenPhim.tenPhim : "Chọn Phim"}
          </button>
          <ExpandMoreIcon />
          <div className="contentPhim">{renderListMovie()}</div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">
            {stateTenRap.tenRap === "" ? "Chọn Rạp" : stateTenRap.tenRap.slice(10)}
          </button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            {listCinema.heThongRapChieu ? renderListCinema() : <a>Chọn Rạp</a>}
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">
            {stateDate.date === "" ? "Ngày Chiếu" : stateDate.date}
          </button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            {listLichChieu.lichChieuPhim ? (
              renderDateCinema()
            ) : (
              <a>Chọn Ngày Chiếu</a>
            )}
          </div>
        </div>

        <div className="dropdownTitle">
          <button className="dropbtn">Xuất Chiếu</button>
          <ExpandMoreIcon />
          <div className="contentTitle">
            {listXuatChieu.lichChieuPhim ? (
              renderTimerCinema()
            ) : (
              <a>Chọn Xuất Chiếu</a>
            )}
          </div>
        </div>

        <button className="btnMuaVe">Mua Vé Ngay</button>
      </div>
    </div>
  );
};
export default DropDowns;
