import React, { Component } from "react";
// action redux thunk
import { connect } from "react-redux";
import { postSignUp_Action } from "../../store/actions/signUp.action";
// material
import { Container } from "@material-ui/core";
import "./scss/signup.css";
import { withStyles } from "@material-ui/core/styles";

// modal
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

const useStyles = () => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
});

class SignUpPage extends Component {
  state = {
    User: {
      taiKhoan: "",
      matKhau: "",
      confirmMatKhau: "",
      email: "",
      soDt: "",
      maNhom: "GP01",
      maLoaiNguoiDung: "KhachHang",
      hoTen: "",
    },
    error: {
      taiKhoan: "",
      matKhau: "",
      confirmMatKhau: "",
      email: "",
      soDt: "",
      hoTen: "",
    },
    valid: false,
    open: false,
  };

  handleClose() {
    this.setState({
      open: false,
    });
  }
  handleChange = (event) => {
    const { name, value, type, pattern } = event.target;
    let valuesUpdate = { ...this.state.User, [name]: value };
    let errorUpdate = { ...this.state.error, [name]: value };
    this.setState(
      {
        User: valuesUpdate,
        error: errorUpdate,
      },
      () => {
        this.validButtonSubmit();
      }
    );
    // check pattern
    let regex = new RegExp(pattern);
    // check empty
    if (value.trim() === "") {
      errorUpdate[name] = " Do Not Empty ! ";
    } else {
      errorUpdate[name] = "";
    }
    // check empty

    // check text
    if (name === "hoTen") {
      if (!regex.test(value)) {
        errorUpdate[name] = "Không Đúng Định Dạng !";
      } else {
        errorUpdate[name] = "";
      }
    }
    // check text

    // check password
    if (name === "confirmMatKhau") {
      if (value === valuesUpdate["matKhau"]) {
        errorUpdate[name] = "";
      } else {
        errorUpdate[name] = "Mật Khẩu Không Trùng Nhau !";
      }
    }
    // check password

    // check email
    if (type === "email") {
      if (!regex.test(value)) {
        errorUpdate[name] = "Email Invaild !";
      } else {
        errorUpdate[name] = "";
      }
    }
    // check email
  };

  handleSubmit = (event) => {
    event.preventDefault();
    this.props.dispatch(postSignUp_Action(this.state.User));
    this.setState({
      open: true,
    });
  };
  refeshInput = () => {
    let elementsInput = document.querySelectorAll("input");
    for (let refesh of elementsInput) {
      refesh.value = "";
    }
  };

  componentDidUpdate() {
    if (this.props.mesageSuccess) {
      this.props.history.replace("/signIn");
    }
  }

  validButtonSubmit() {
    let validS = true;
    for (let key in this.state.error) {
      if (this.state.error[key] !== "" || this.state.User[key] === "") {
        validS = false;
      }
    }
    console.log("1", validS);
    this.setState({
      ...this.state,
      valid: validS,
    });
  }

  render() {
    console.log("2", this.state.valid);
    const { classes } = this.props;
    const { User, error } = this.state;
    return (
      <div className="backgroundSignUp">
        <div className="wrapSignUp">
          <Container maxWidth="md">
            <h4>Form Đăng Ký</h4>
            <form onSubmit={this.handleSubmit}>
              <input
                onChange={this.handleChange}
                value={User.taiKhoan}
                placeholder="Tài Khoản"
                name="taiKhoan"
                type="text"
              />
              <span>{error.taiKhoan}</span>
              <input
                onChange={this.handleChange}
                value={User.matKhau}
                placeholder="Mật Khẩu"
                name="matKhau"
                type="password"
              />
              <span>{error.matKhau}</span>
              <input
                onChange={this.handleChange}
                value={User.confirmMatKhau}
                placeholder="Xác Nhận Mật Khẩu Mật Khẩu"
                name="confirmMatKhau"
                type="password"
              />
              <span>{error.confirmMatKhau}</span>

              <input
                onChange={this.handleChange}
                value={User.email}
                placeholder="Email"
                name="email"
                type="email"
                pattern="^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$"
              />
              <span>{error.email}</span>

              <input
                onChange={this.handleChange}
                value={User.soDt}
                placeholder="Số Điện Thoại"
                name="soDt"
              />
              <span>{error.soDt}</span>

              <input placeholder="Khách Hàng" disabled />

              <input
                onChange={this.handleChange}
                value={User.hoTen}
                placeholder="User Name"
                name="hoTen"
                type="text"
                pattern="^[A-Za-z]+$"
              />
              <span>{error.hoTen}</span>
              <br />
              <br />
              <span>{this.props.messageSignUp}</span>
              <div style={{ textAlign: "center" }}>
                {this.state.valid ? (
                  <button type="submit">Đăng Ký</button>
                ) : (
                  <button type="submit" disabled>
                    Đăng Ký
                  </button>
                )}
              </div>
            </form>
          </Container>
        </div>
        <Modal
          aria-labelledby="transition-modal-title"
          aria-describedby="transition-modal-description"
          className={classes.modal}
          open={this.state.open}
          onClose={this.handleClose}
          closeAfterTransition
          BackdropComponent={Backdrop}
          BackdropProps={{
            timeout: 500,
          }}
        >
          <Fade in={this.state.open}>
            <div className="modalTitle"></div>
          </Fade>
        </Modal>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    messageSignUp: state.SignUpReducer.messageSignUp,
    mesageSuccess: state.SignUpReducer.mesageSuccess,
  };
};

export default withStyles(useStyles)(connect(mapStateToProps)(SignUpPage));
